const input = $input.first().json;
const body = input.body || input;

const client_id = body.client_id;
if (!client_id) throw new Error('Missing required field: client_id');

const validTypes = ['weekly', 'monthly'];
const reportType = (body.report_type || 'weekly').toLowerCase();
if (!validTypes.includes(reportType)) throw new Error('Invalid report_type. Must be: weekly or monthly');

return [{ json: {
  client_id,
  report_type: reportType,
  include_costs: body.include_costs !== false
}}];

const costData = $('GET Cost Summary').first().json || {};
const pipelineData = $('GET Pipeline Dashboard').first().json || {};
const healthData = $input.first().json || {};
const validated = $('Validate Input').first().json;

const dataSources = [];
if (!costData.error && !costData.statusCode) dataSources.push('costs');
if (!pipelineData.error && !pipelineData.statusCode) dataSources.push('pipeline');
if (!healthData.error && !healthData.statusCode) dataSources.push('client_health');

const message = 'Generate ' + validated.report_type + ' operations report for client ' + validated.client_id + '. Include analysis of costs, pipeline status, and client health.';

return [{ json: {
  persona: 'andrew',
  message: message,
  client_id: validated.client_id,
  report_type: validated.report_type,
  include_costs: validated.include_costs,
  cost_data: costData,
  pipeline_data: pipelineData,
  health_data: healthData,
  data_sources: dataSources
}}];

const res = $input.first().json;
const mergedData = $('Merge All Data').first().json;
const validated = $('Validate Input').first().json;
const costData = mergedData.cost_data || {};
const pipelineData = mergedData.pipeline_data || {};
const healthData = mergedData.health_data || {};

const narrative = res.message || res.response || res.content || 'Operations report generated by Andrew persona';

const totalCost = costData.total_cost || costData.totalCost || 0;
const avgMargin = costData.avg_margin || costData.avgMargin || 0;

const report = [
  '# Operations Report â€” ' + validated.report_type.charAt(0).toUpperCase() + validated.report_type.slice(1),
  '',
  '**Client:** ' + validated.client_id,
  '**Period:** ' + validated.report_type,
  '**Generated:** ' + new Date().toISOString(),
  '**Data Sources:** ' + mergedData.data_sources.join(', '),
  '',
  '## Summary',
  narrative,
  ''
];

if (validated.include_costs) {
  report.push('## Cost Breakdown');
  report.push('- **Total Cost:** $' + Number(totalCost).toFixed(2));
  report.push('- **Avg Margin:** ' + Number(avgMargin).toFixed(1) + '%');
  report.push('');
}

const pipelineSummary = {
  active_projects: pipelineData.active_projects || pipelineData.total || 0,
  stalled: pipelineData.stalled || 0,
  completed: pipelineData.completed || 0
};

const healthStatus = {
  overall: healthData.status || healthData.overall || 'unknown',
  score: healthData.score || healthData.health_score || 0,
  alerts: healthData.alerts || []
};

return [{ json: {
  report_markdown: report.join('\n'),
  total_cost: Number(totalCost).toFixed(2),
  avg_margin: Number(avgMargin).toFixed(1),
  period: validated.report_type,
  client_id: validated.client_id,
  pipeline_summary: pipelineSummary,
  health_status: healthStatus,
  data_sources: mergedData.data_sources,
  generated_at: new Date().toISOString()
}}];
