const input = $input.first().json;
const body = input.body || input;

const clientIds = body.client_ids || [];
if (!Array.isArray(clientIds) || clientIds.length === 0) throw new Error('Missing required field: client_ids (array)');
if (clientIds.length > 5) throw new Error('Maximum 5 client_ids allowed');

const briefingType = body.briefing_type || 'daily';
if (!['daily', 'weekly'].includes(briefingType)) throw new Error('briefing_type must be daily or weekly');

// Emit one item per client_id for SplitInBatches
return clientIds.map(id => ({ json: { client_id: id, briefing_type: briefingType, total_clients: clientIds.length } }));

const items = $input.all();
const briefings = items.map((item, i) => {
  const res = item.json;
  const clientId = $('Validate & Split').all()[i]?.json?.client_id || 'unknown-' + i;
  const briefingType = $('Validate & Split').all()[i]?.json?.briefing_type || 'daily';
  return {
    client_id: clientId,
    briefing: res.message || res.response || res.content || 'Briefing for ' + clientId,
    briefing_type: briefingType,
    generated_at: new Date().toISOString()
  };
});

return [{ json: {
  briefings,
  total: briefings.length,
  briefing_type: briefings[0]?.briefing_type || 'daily',
  generated_at: new Date().toISOString()
}}];
