const input = $input.first().json;
const body = input.body || input;

const customer_name = body.customer_name;
if (!customer_name) throw new Error('Missing required field: customer_name');

return [{ json: {
  customer_name,
  company: body.company || '',
  last_interaction: body.last_interaction || '',
  deal_stage: body.deal_stage || 'discovery'
}}];

const res = $input.first().json;
const validated = $('Validate Input').first().json;

// Error check: if response has error field or HTTP error
const hasError = res.error || res.statusCode >= 400 || res.code === 'ETIMEDOUT';
const isRetryable = res.statusCode === 503;

return [{ json: {
  response: res,
  validated,
  has_error: !!hasError,
  is_retryable: !!isRetryable
}}];

// Wait 5 seconds before retry
await new Promise(resolve => setTimeout(resolve, 5000));
const validated = $json.validated;
return [{ json: validated }];

const res = $input.first().json;
const errorCheck = $('Error Check').first().json;
const validated = errorCheck.validated;
const originalRes = errorCheck.has_error ? res : errorCheck.response;

const stages = {
  discovery: 'Schedule discovery call within 48 hours',
  proposal: 'Send proposal follow-up within 24 hours',
  negotiation: 'Prepare counter-offer or schedule closing call',
  closed_won: 'Send onboarding welcome package',
  closed_lost: 'Add to nurture sequence for 90-day re-engage'
};

const templateMap = {
  negotiation: 'closing-template',
  discovery: 'intro-template',
  proposal: 'proposal-template'
};

const dealStage = originalRes.deal_stage || validated.deal_stage || 'discovery';
const crmNeeded = dealStage === 'negotiation' || dealStage === 'closed_won' || dealStage === 'closed_lost';

return [{ json: {
  follow_up_message: originalRes.message || originalRes.response || originalRes.content || 'Follow-up generated by Daniel persona',
  next_action: stages[dealStage] || 'Review account and schedule next touchpoint',
  deal_stage: dealStage,
  customer_name: originalRes.customer_name || validated.customer_name,
  company: originalRes.company || validated.company,
  recommended_template: templateMap[dealStage] || 'general-followup-template',
  crm_update_needed: crmNeeded,
  priority: dealStage === 'negotiation' ? 'high' : 'medium',
  sent_at: new Date().toISOString()
}}];
