const input=$input.first().json;const query=input.query||input.params||{};
const staticData=$getWorkflowStaticData('global');
const errors=staticData.errors||[];
let filtered=[...errors];
const statusFilter=(query.status||'all').toLowerCase();
if(statusFilter!=='all')filtered=filtered.filter(e=>e.status===statusFilter);
if(query.severity)filtered=filtered.filter(e=>e.severity===query.severity);
if(query.category)filtered=filtered.filter(e=>e.category===query.category);
const limit=parseInt(query.limit)||20;
filtered=filtered.slice(0,limit);
const summary={pending:errors.filter(e=>e.status==='pending').length,retried:errors.filter(e=>e.status==='retried').length,resolved:errors.filter(e=>e.status==='resolved').length,by_category:{}};
for(const e of errors){summary.by_category[e.category]=(summary.by_category[e.category]||0)+1;}
return [{json:{total:errors.length,filtered:filtered.length,errors:filtered,summary}}];
