const input=$input.first().json;const body=input.body||input;
if(!body.error_id)throw new Error('Missing: error_id');
if(!body.resolution_note)throw new Error('Missing: resolution_note');
const staticData=$getWorkflowStaticData('global');
const errors=staticData.errors||[];
const idx=errors.findIndex(e=>e.error_id===body.error_id);
if(idx<0)throw new Error('Error not found: '+body.error_id);
errors[idx].status='resolved';
errors[idx].resolved_at=new Date().toISOString();
errors[idx].resolution_note=body.resolution_note;
staticData.errors=errors;
return [{json:{resolved:true,error_id:body.error_id,resolution_note:body.resolution_note}}];
