const input = $input.first().json;
const body = input.body || input;

if (!body.customer_name) throw new Error('Missing required field: customer_name');
if (!body.original_request) throw new Error('Missing required field: original_request');
if (!body.trigger_type) throw new Error('Missing required field: trigger_type');
if (!body.output_action) throw new Error('Missing required field: output_action');

const projectId = 'proj-' + Date.now() + '-' + Math.random().toString(36).substring(2, 8);

return [{ json: {
  project_id: projectId,
  customer_name: body.customer_name,
  original_request: body.original_request,
  ticket_summary: body.ticket_summary || body.original_request,
  business_objective: body.business_objective || body.original_request,
  trigger_type: body.trigger_type,
  trigger_description: body.trigger_description || 'Triggered via webhook',
  processing_steps: body.processing_steps || ['process_data via http'],
  output_action: body.output_action,
  expected_output: body.expected_output || 'JSON response with processed data'
}}];

const res = $input.first().json;
const validated = $('Validate Input').first().json;

const planDict = res.plan_dict || res.plan || res;
const confidence = res.confidence || {};
const confScore = confidence.overall || confidence.score || 0;
const brief = res.delivery_report || res.brief || validated.original_request;

return [{ json: {
  project_id: validated.project_id,
  plan_dict: planDict,
  confidence: confidence,
  confidence_score: confScore,
  brief: brief,
  ready_for_build: res.ready_for_build || false,
  validated: validated
}}];

const verifyRes = $input.first().json;
const planData = $('Extract Plan').first().json;

return [{ json: {
  ...planData,
  verification: {
    verified: verifyRes.verified || verifyRes.passed || false,
    confidence_score: verifyRes.confidence_score || verifyRes.score || 0,
    issues: verifyRes.issues || []
  },
  auto_advanced: true
}}];

const planData = $input.first().json;

return [{ json: {
  ...planData,
  verification: { verified: false, confidence_score: 0, issues: ['Skipped: confidence too low'] },
  auto_advanced: false
}}];

const costRes = $input.first().json;
const prev = $('Extract Verification').isValid ? $('Extract Verification').first().json : $('Skip Verification').first().json;

const costEstimate = {
  estimated_cost_usd: costRes.estimated_cost || costRes.cost || 0,
  token_estimate: costRes.tokens || costRes.token_estimate || 0
};

return [{ json: {
  ...prev,
  cost_estimate: costEstimate
}}];

const prev = $('Extract Cost').first().json;
const memRes = $input.first().json;

return [{ json: {
  project_id: prev.project_id,
  plan: prev.plan_dict,
  verification: prev.verification,
  cost_estimate: prev.cost_estimate,
  next_stage: prev.auto_advanced ? 'build' : 'review',
  pipeline_status: prev.ready_for_build ? 'ready' : 'pending_review',
  ready_for_build: prev.ready_for_build && prev.auto_advanced,
  memory_stored: !memRes.error,
  created_at: new Date().toISOString()
}}];
