const input = $input.first().json;
const body = input.body || input;

const client_name = body.client_name;
if (!client_name) throw new Error('Missing required field: client_name');

return [{ json: {
  client_name,
  meeting_type: body.meeting_type || 'general',
  agenda_items: body.agenda_items || [],
  attendees: body.attendees || []
}}];

const res = $input.first().json;
const validated = $('Validate Input').first().json;

const brief = res.message || res.response || res.content || 'Meeting brief generated by Rebecka persona';

const agendaItems = validated.agenda_items || [];
const agendaFormatted = agendaItems.length > 0
  ? agendaItems.map((item, i) => (i + 1) + '. ' + item).join('\n')
  : '1. Introductions\n2. Agenda review\n3. Open discussion\n4. Next steps';

const attendeeList = validated.attendees || [];

const prepNotes = [
  '**Meeting Type:** ' + validated.meeting_type,
  '**Client:** ' + validated.client_name,
  '**Attendees:** ' + (attendeeList.length > 0 ? attendeeList.join(', ') : 'TBD'),
  '',
  '### Preparation Notes',
  brief,
  '',
  '### Agenda',
  agendaFormatted
].join('\n');

// Calendar .ics block
const now = new Date();
const start = new Date(now.getTime() + 24 * 60 * 60 * 1000);
const end = new Date(start.getTime() + 60 * 60 * 1000);
const fmt = (d) => d.toISOString().replace(/[-:]/g, '').replace(/\.\d{3}/, '');
const calendarBlock = [
  'BEGIN:VCALENDAR',
  'VERSION:2.0',
  'PRODID:-//ManageAI//n8n//EN',
  'BEGIN:VEVENT',
  'DTSTART:' + fmt(start),
  'DTEND:' + fmt(end),
  'SUMMARY:' + validated.meeting_type + ' - ' + validated.client_name,
  'DESCRIPTION:' + agendaFormatted.replace(/\n/g, '\\n'),
  attendeeList.length > 0 ? attendeeList.map(a => 'ATTENDEE:mailto:' + a).join('\n') : '',
  'END:VEVENT',
  'END:VCALENDAR'
].filter(l => l).join('\n');

// Pre-read suggestions
const preRead = [];
const mtLower = validated.meeting_type.toLowerCase();
if (mtLower.includes('review')) {
  preRead.push('Review project status');
  preRead.push('Check last activity');
}
if (mtLower.includes('quarterly')) {
  preRead.push('Review quarterly metrics');
}
if (mtLower.includes('planning') || mtLower.includes('kickoff')) {
  preRead.push('Review project scope document');
}

// Follow-up email template
const followUpTemplate = 'Subject: Follow-up: ' + validated.meeting_type + ' with ' + validated.client_name + '\n\nHi team,\n\nThank you for attending today\'s ' + validated.meeting_type + ' meeting.\n\nKey Takeaways:\n- [Insert key points discussed]\n\nAction Items:\n- [Insert action items with owners and deadlines]\n\nNext Steps:\n- [Insert next meeting date or follow-up plan]\n\nPlease let me know if I missed anything.\n\nBest regards';

return [{ json: {
  meeting_brief: brief,
  agenda_formatted: agendaFormatted,
  prep_notes: prepNotes,
  send_to: attendeeList,
  client_name: validated.client_name,
  meeting_type: validated.meeting_type,
  calendar_block: calendarBlock,
  pre_read: preRead,
  follow_up_email_template: followUpTemplate,
  prepared_at: new Date().toISOString()
}}];
