const input = $input.first().json;
const body = input.body || input;

const topic = body.topic;
if (!topic) throw new Error('Missing required field: topic');

const validFormats = ['blog', 'email', 'social'];
const fmt = (body.format || 'blog').toLowerCase();
if (!validFormats.includes(fmt)) throw new Error('Invalid format. Must be: blog, email, or social');

return [{ json: {
  topic,
  format: fmt,
  target_audience: body.target_audience || 'general',
  tone: body.tone || 'professional'
}}];

const res = $input.first().json;
const validated = $('Validate Input').first().json;
const content = res.message || res.response || res.content || 'Content generated by Sarah persona';

// Word count
const words = content.split(/\s+/).filter(w => w.length > 0);
const wordCount = words.length;

// Reading time: word_count / 200, rounded to nearest 0.5
const rawTime = wordCount / 200;
const readingTime = Math.round(rawTime * 2) / 2 || 0.5;

// SEO keywords: words > 5 chars, top 5 by frequency
const lowerWords = content.toLowerCase().replace(/[^a-z\s]/g, '').split(/\s+/).filter(w => w.length > 5);
const freq = {};
for (const w of lowerWords) { freq[w] = (freq[w] || 0) + 1; }
const seoKeywords = Object.entries(freq)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 5)
  .map(e => e[0]);

// Format-specific metadata
let formatMetadata = {};
const fmt = validated.format;

if (fmt === 'blog') {
  const h2Count = (content.match(/##/g) || []).length;
  const introParagraph = content.substring(0, 150);
  formatMetadata = { h2_count: h2Count, intro_paragraph: introParagraph };
} else if (fmt === 'email') {
  const firstLine = content.split('\n')[0] || '';
  const lowerContent = content.toLowerCase();
  const ctaDetected = lowerContent.includes('click') || lowerContent.includes('sign up') || lowerContent.includes('get started');
  formatMetadata = { subject_line: firstLine, cta_detected: ctaDetected };
} else if (fmt === 'social') {
  const charCount = content.length;
  let platformFit = 'long-form';
  if (charCount <= 280) platformFit = 'Twitter';
  else if (charCount <= 3000) platformFit = 'LinkedIn';
  formatMetadata = { character_count: charCount, platform_fit: platformFit };
}

const formatMeta = {
  blog: { label: 'Blog Post', minWords: 300 },
  email: { label: 'Email', minWords: 50 },
  social: { label: 'Social Post', minWords: 20 }
};
const meta = formatMeta[fmt] || formatMeta.blog;

return [{ json: {
  content,
  format: fmt,
  format_label: meta.label,
  topic: validated.topic,
  target_audience: validated.target_audience,
  tone: validated.tone,
  word_count: wordCount,
  seo_keywords: seoKeywords,
  reading_time_minutes: readingTime,
  format_metadata: formatMetadata,
  created_at: new Date().toISOString()
}}];
